import React, { useState, useEffect, useRef } from 'react';
import {
  FaList,
  FaTh,
  FaCalendarAlt,
  FaPlus
} from 'react-icons/fa';
import List from './List'; // List bileşeni
import BoardView from './BoardView'; // Board bileşeni
import CalendarView from './CalendarView'; // Calendar bileşeni
import './EverythingView.css'; // CSS dosyası

const EverythingView = () => {
  const [viewMode, setViewMode] = useState('list'); // Varsayılan görünüm 'list'
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Menü açma/kapatma kontrolü
  const dropdownRef = useRef(null);

  // Dropdown menüsünü açma/kapatma
  const toggleDropdown = (e) => {
    e.stopPropagation();
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Menü dışına tıklandığında menüyü kapatma
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    window.addEventListener('click', handleClickOutside);
    
    return () => {
      window.removeEventListener('click', handleClickOutside);
    };
  }, []);

  const handleListButtonClick = () => {
    if (viewMode === 'list') {
      // Zaten list görünümündeysek menüyü aç/kapat
      setIsDropdownOpen(!isDropdownOpen);
    } else {
      // List görünümüne geç
      setViewMode('list');
      setIsDropdownOpen(false); // Görünüm değiştirildiği için menüyü kapat
    }
  };

  const renderView = () => {
    switch (viewMode) {
      case 'list':
        return <List />;
      case 'board':
        return <BoardView />;
      case 'calendar':
        return <CalendarView />;
      default:
        return <List />;
    }
  };

  return (
    <div className="everything-container">
      {/* Üst Menü */}
      <div className="everything-header">
        <div className="view-options">
          <button
            className={`view-btn ${viewMode === 'list' ? 'active' : ''}`}
            onClick={handleListButtonClick}
          >
            <FaList /> List
          </button>
          <button
            className={`view-btn ${viewMode === 'board' ? 'active' : ''}`}
            onClick={() => setViewMode('board')}
          >
            <FaTh /> Board
          </button>
          <button
            className={`view-btn ${viewMode === 'calendar' ? 'active' : ''}`}
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt /> Calendar
          </button>
          <button className="view-btn">
            <FaPlus /> +View
          </button>
        </div>
      </div>

      {/* İçerik */}
      <div className="everything-content">
        {renderView()}
      </div>

      {/* Dropdown menü */}
      {isDropdownOpen && viewMode === 'list' && (
        <div className="dropdown-menu" ref={dropdownRef}>
          <button>Rename</button>
          <button>Copy link to view</button>
          <button>Add to favorites</button>
          <button>Customize view</button>
          <div className="toggle">
            <span>Pin view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span>Private view</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span>Autosave for me</span>
            <input type="checkbox" />
          </div>
          <div className="toggle">
            <span>Set as default view</span>
            <input type="checkbox" />
          </div>
          <div className="submenu">
            <button>Export view</button>
            <button>Duplicate view</button>
          </div>
          <button>Delete view</button>
          <button>Sharing & Permissions</button>
        </div>
      )}
    </div>
  );
};

export default EverythingView;
