import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ListView from './components/EverythingView/ListView';
import BoardView from './components/EverythingView/BoardView';
import GanttView from './components/EverythingView/GanttView';
import CalendarView from './components/EverythingView/CalendarView';
import TableView from './components/EverythingView/TableView';

const App = () => {
  const [activeTab, setActiveTab] = useState('list');

  return (
    <Router>
      <div className="app-container">
        {/* Üst Menü */}
        <nav className="top-menu">
          <ul>
            <li>
              <Link
                to="/list"
                className={activeTab === 'list' ? 'active' : ''}
                onClick={() => setActiveTab('list')}
              >
                List
              </Link>
            </li>
            <li>
              <Link
                to="/board"
                className={activeTab === 'board' ? 'active' : ''}
                onClick={() => setActiveTab('board')}
              >
                Board
              </Link>
            </li>
            <li>
              <Link
                to="/calendar"
                className={activeTab === 'calendar' ? 'active' : ''}
                onClick={() => setActiveTab('calendar')}
              >
                Calendar
              </Link>
            </li>
            <li>
              <Link
                to="/gantt"
                className={activeTab === 'gantt' ? 'active' : ''}
                onClick={() => setActiveTab('gantt')}
              >
                Gantt
              </Link>
            </li>
            <li>
              <Link
                to="/table"
                className={activeTab === 'table' ? 'active' : ''}
                onClick={() => setActiveTab('table')}
              >
                Table
              </Link>
            </li>
          </ul>
        </nav>

        {/* İçerik */}
        <div className="main-content">
          <Routes>
            <Route path="/list" element={<ListView />} />
            <Route path="/board" element={<BoardView />} />
            <Route path="/calendar" element={<CalendarView />} />
            <Route path="/gantt" element={<GanttView />} />
            <Route path="/table" element={<TableView />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
