import React, { useState } from 'react';
import './AssignedToMe.css';
import { FaPlus, FaTrash } from 'react-icons/fa';
import TaskModal from './TaskModal'; // Görev ekleme modal bileşeni

const AssignedToMe = () => {
  const [showSettings, setShowSettings] = useState(false); // More settings menüsü için state
  const [showTaskModal, setShowTaskModal] = useState(false); // Görev ekleme ekranı için state

  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const removeCard = () => {
    alert("Card removed!"); // Remove card işlemi
  };

  const toggleTaskModal = () => {
    setShowTaskModal(!showTaskModal); // Görev modal'ını açıp kapatma
  };

  return (
    <div className="assigned-to-me-container">
      <div className="assigned-to-me-header">
        <h2>Assigned to me</h2>
        <button className="icon-btn" onClick={toggleTaskModal} title="Add task">
          <FaPlus />
        </button>
      </div>

      {showTaskModal && <TaskModal closeModal={toggleTaskModal} />} {/* Görev ekleme modalı */}

      {showSettings && (
        <div className="more-settings-menu">
          <button className="settings-item" onClick={removeCard}>
            <FaTrash className="trash-icon" /> Remove card
          </button>
        </div>
      )}

      <div className="assigned-to-me-content">
        <div className="icon-placeholder">
          <span>✔️</span> {/* Placeholder için simge */}
        </div>
        <p>Tasks assigned to you will appear here. <a href="#">Learn more</a></p>
        <button className="add-task-btn" onClick={toggleTaskModal}>+ Add task</button>
      </div>
    </div>
  );
};

export default AssignedToMe;
